// src/pages/AboutUs.js

import React from 'react';
import './AboutUs.css';
import { VerticalTimeline, VerticalTimelineElement } from 'react-vertical-timeline-component';
import 'react-vertical-timeline-component/style.min.css';
import ZetaClass from '../assets/ZetaClass.png'; // Import the image

const events = [
    { date: '1904-10-15', title: 'Founding of Theta Tau', description: 'Theta Tau was founded at the University of Minnesota.' },
    { date: '1920-01-01', title: 'Expansion', description: 'Theta Tau began to expand to multiple universities across the United States.' },
    { date: '1960-01-01', title: 'Continued Growth', description: 'Theta Tau continued to grow and develop, establishing chapters nationwide.' },
    { date: '2023-01-01', title: 'Chapter at Santa Clara University', description: 'The Upsilon Epsilon Chapter of Theta Tau was established at Santa Clara University.' },
];

const AboutUs = () => {
    return (
        <div className="about-us">
            <header className="hero" style={{ backgroundImage: `url(${ZetaClass})` }}>
                <h1 className="title">ABOUT US</h1>
                <h2 className="subtitle">UPSILON EPSILON CHAPTER</h2>
            </header>

            <section className="history">
                <h2>Our History</h2>
                <p>Theta Tau is the nation's largest and oldest professional co-ed engineering fraternity. Our fraternity was founded on October 15, 1904, at the University of Minnesota.</p>
                <p>Throughout the years, Theta Tau has grown significantly. The fraternity started expanding to other universities in 1920 and continued to grow stronger over the decades. In 2023, the Upsilon Epsilon Chapter of Theta Tau was established at Santa Clara University, providing a community for students driven by engineering excellence and camaraderie.</p>
            </section>

            <section className="interactive-timeline">
                <h2>Interactive Timeline</h2>
                <VerticalTimeline>
                    {events.map((event, idx) => (
                        <VerticalTimelineElement
                            key={idx}
                            date={event.date}
                            iconStyle={{ background: 'rgb(33, 150, 243)', color: '#fff' }}
                            icon={<i className="fas fa-history"></i>}
                        >
                            <h3 className="vertical-timeline-element-title">{event.title}</h3>
                            <p>{event.description}</p>
                        </VerticalTimelineElement>
                    ))}
                </VerticalTimeline>
            </section>
        </div>
    );
};

export default AboutUs;
