// src/components/dashboard/widgets/BroDate.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Brodate.css';

const BroDate = () => {
    const [group, setGroup] = useState([]);

    useEffect(() => {
        const fetchBroDateGroup = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://localhost:5001/api/brodate-group', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                setGroup(response.data.group);
            } catch (error) {
                console.error('Error fetching BroDate group:', error);
            }
        };

        fetchBroDateGroup();
    }, []);

    return (
        <div className="brodate-widget">
            <h2>BroDate Group</h2>
            {group.length > 0 ? (
                <ul>
                    {group.map((member) => (
                        <li key={member.email}>{member.firstName} {member.lastName} ({member.email})</li>
                    ))}
                </ul>
            ) : (
                <p>No BroDate group assigned for this week.</p>
            )}
        </div>
    );
};

export default BroDate;
