// src/pages/Rush.js

import React, {useEffect, useState} from 'react';
import './Rush.css';
import ZetaClassImage from '../assets/ZetaClass.png'; // Import the background image

const Rush = () => {
    const calculateTimeLeft = () => {
        const targetDate = new Date("2025-01-01T00:00:00");
        const now = new Date();
        const difference = targetDate - now;
        let timeLeft = {};

        if (difference > 0) {
            timeLeft = {
                days: Math.floor(difference / (1000 * 60 * 60 * 24)),
                hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
                minutes: Math.floor((difference / 1000 / 60) % 60),
                seconds: Math.floor((difference / 1000) % 60),
            };
        }
        return timeLeft;
    };

    const [timeLeft, setTimeLeft] = useState(calculateTimeLeft());

    useEffect(() => {
        const timer = setTimeout(() => {
            setTimeLeft(calculateTimeLeft());
        }, 1000);

        return () => clearTimeout(timer);
    }, [timeLeft]);

    return (
        <div className="rush">
            <div className="rush-hero" style={{backgroundImage: `url(${ZetaClassImage})`}}>
                <h1 className="title">WINTER RUSH 2025</h1>
            </div>
            <div className="countdown-section">
                <h2 className="countdown-title">Countdown to Rush</h2>
                <div className="countdown">
                    {Object.keys(timeLeft).length ? (
                        Object.keys(timeLeft).map((interval) => (
                            <div key={interval} className="countdown-element">
                                {timeLeft[interval]} {interval}
                            </div>
                        ))
                    ) : (
                        <span>Time's up!</span>
                    )}
                </div>
            </div>
            <div className="week-calendar">
                {['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'].map((day, index) => (
                    <div key={index} className="day-card">
                        <h3>{day}</h3>
                        <p>Add info here</p>
                    </div>
                ))}
            </div>
            <p>Information about rushing Theta Tau.</p>
        </div>
    );
};

export default Rush;
